#question 1: nombre de journalistes dans l'array
def number_handle_counter(tab)
  number_handle = 0
  number_handle = tab.length
  puts number_handle
  return number_handle
end

# number_handle = number_handle_counter(tab)

#nombre de handle avec un nombre
def number_handle_with_number(tab, number_handle)
  nb = 0
  tab.each do |var|
    # puts var
    if var =~ /\d/ # Calling String's =~ method.
      # puts "The String #{var} has a number in it."
      nb += 1
    else
      # puts "The String #{var} does not have a number in it."
    end
  end
  puts "les #{number_handle} Handles contiennent #{nb} nombres"
end

# number_handle_with_number(tab, number_handle)

#handle contiennent les 4 lettres du prénom "Aude" à la suite
def number_handle_with_aude(tab, number_handle)
  nb = 0
  tab.each do |var|
    var = var.downcase
    if var.include? "aude"
      # puts "String includes 'aude'"
      nb += 1
    end
  end
  puts "les #{number_handle} Handles contiennent #{nb} fois le nom aude"
end

# number_handle_with_aude(tab, number_handle)

#nombre de handles commencant par une majuscule après le "@"
def number_handle_with_upper_first(tab, number_handle)
  nb = 0
  tab.each do |var|
    # print var.chars
    char = var[1]
    if char === char.capitalize
      # puts 'Character is uppercase.'
      nb += 1
    else
      # puts 'Character is lowercase.'
    end
    # puts
  end
  puts "les #{number_handle} Handles contiennent #{nb} fois une majuscule en premiere lettre"
end

# number_handle_with_upper_first(tab, number_handle)

#nombre de handles contenant une majuscule
def number_handle_with_upper(tab, number_handle)
  nb = 0
  tab.each do |var|
    n = var.count("ABCDEFGHIJKLMNOPQRSTUVWXYZ")
    if n >= 1
      nb += 1
    end
  end
  puts "les #{number_handle} Handles contiennent #{nb} fois une majuscule au moins"
end

# number_handle_with_upper(tab, number_handle)

#Combien y a-t-il de underscore _ dans tous les pseudos confondus
def number_underscore(tab, number_handle)
  nb = 0
  tab.each do |var|
    n = var.count("_")
    # puts "#{var}  #{n}"
    nb += n
  end
  puts "les #{number_handle} Handles contiennent #{nb} fois un underscore"
end

# number_underscore(tab, number_handle)

#Combien y a-t-il de pseudo contenant au moins un underscore _ dans tous les pseudos confondus
def number_handle_with_underscore(tab, number_handle)
  nb = 0
  tab.each do |var|
    n = var.count("_")
    if n >= 1
      nb += 1
    end
  end
  puts "les #{number_handle} Handles contiennent #{nb} qui présentent au moins un underscore"
end

# number_handle_with_underscore(tab, number_handle)

# tri alphabet
# tab=tab.sort { |a, b| a.upcase <=> b.upcase }
def handle_alpha(tab, number_handle)
  tab_s_a = tab.sort do |a, b|
    a.upcase <=> b.upcase
  end
  puts "Handle par ordre alpha ="
  print tab_s_a
  puts
end

# handle_alpha(tab, number_handle)

#tri by length a
def handle_by_length(tab, number_handle)
  tab_s_l = tab.sort_by(&:length)
  puts "50 Plus court="
  puts tab_s_l[0, 50]
end

# handle_by_length(tab, number_handle)

def handle_epenser(tab, number_handle)
  puts
  puts "le Handle @epenser se trouve a l'index #{tab.index("@epenser")} sur #{number_handle}"
end

# handle_epenser(tab, number_handle)

def perform(tab, number_handle)
  number_handle_counter(tab)
  number_handle_with_number(tab, number_handle)
  number_handle_with_aude(tab, number_handle)
  number_handle_with_upper_first(tab, number_handle)
  number_handle_with_upper(tab, number_handle)
  number_underscore(tab, number_handle)
  number_handle_with_underscore(tab, number_handle)
  handle_alpha(tab, number_handle)
  handle_by_length(tab, number_handle)
  handle_epenser(tab, number_handle)
end

perform(tab, tab.length)
